
public class qaclass16 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//for loop
		for(int j=1; j<10; ++j){
			System.out.println(j);
		}
		
		//while loops
		
		 //I need to print the k value from 2 to 6
		int k=2;
		while(k<=6)
		{
			System.out.println("before increment"+k);
			k++;
			//System.out.println("after increment"+k);
		}
				
		

	}

}
